CREATE TABLE Categories (cat_name TEXT PRIMARY KEY UNIQUE NOT NULL);
CREATE TABLE Operations (op_name TEXT PRIMARY KEY UNIQUE NOT NULL);
CREATE TABLE Actions_log (id INTEGER PRIMARY KEY UNIQUE NOT NULL,action_datetime DATETIME NOT NULL,optype TEXT REFERENCES Operations (op_name) ON DELETE SET NULL ON UPDATE CASCADE,category TEXT REFERENCES Categories (cat_name) ON DELETE SET NULL ON UPDATE CASCADE,amount REAL (16, 2) NOT NULL,comment TEXT);